import React,{useState,useEffect} from 'react';
import './analogue-clock.style.css';
import moment from 'moment';

 function AnalogueClock(props){
    const {timezone} = props;
     let date = new Date(new Date().toLocaleString("en-US", {timeZone: timezone}));
    const [hours,setHours] = useState(0);
    const [minutes,setMinutes] = useState(0);
    const [seconds,setSeconds] = useState(0);



     useEffect(() => {
         let interval = null;
         interval = setInterval(() => {
             date = new Date(new Date().toLocaleString("en-US", {timeZone: timezone}));
             setHours(((date.getHours() + 11) % 12 + 1));
             setMinutes( date.getMinutes());
             setSeconds(date.getSeconds());
         }, 1000);

         return () => clearInterval(interval);

     }, [minutes,hours, seconds]);



    return(
        <div class="clock">
            <div class="wrap">
                <span class="hour" style={{transform:`rotate(${hours * 30}deg)`}}></span>
                <span class="minute" style={{transform:`rotate(${ minutes * 6}deg)`}}></span>
                <span class="second" style={{transform:`rotate(${seconds * 6}deg)`}}></span>
                <span class="dot"></span>
            </div>
        </div>
    )
}

export default AnalogueClock;
